import { motion } from "framer-motion";

// styles
import "./styles.module.scss";
import { Fragment, useState } from "react";

// components
import SingleSite from "../singleSite";

const WebsitesIcon = () => {
  const [websitesOpen, toggleWebsitesOpen] = useState(false);
  function handleClick() {
    toggleWebsitesOpen(!websitesOpen);
    console.log(`the button was clicked`);
  }
  return (
    <Fragment>
      {websitesOpen ? (
        <div className="margin-container">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.25 }}
            className="websites-window"
          >
            <p className="close-button" onClick={handleClick}>
              &#10006;
            </p>
            <SingleSite />
          </motion.div>
        </div>
      ) : (
        <div></div>
      )}
    </Fragment>
  );
};

export default WebsitesIcon;
