import { motion } from "framer-motion";

// styles
import "./styles.module.scss";
import { Fragment, useState } from "react";

// components
import SingleSite from "../singleSite";

const WebsitesIcon = () => {
  const [websitesOpen, toggleWebsitesOpen] = useState(false);
  function handleClick() {
    toggleWebsitesOpen(!websitesOpen);
    console.log(`the button was clicked`);
  }
  return (
    <Fragment>
      <div className="single-icon" onClick={handleClick}>
        <img src="/icon.png" alt="preferences" />
        <p className="icon-title">preferences</p>
      </div>
    </Fragment>
  );
};

export default WebsitesIcon;
