import Draggable, { DraggableCore } from "react-draggable";
import { motion } from "framer-motion";
import "../home/styles.scss";
import { useState, useRef } from "react";

//componenets
import Window from "../../components/window";
import Websites from "../../components/websites";
import PreferencesIcon from "../../components/Preferences";
import PreferencesWindow from "../../components/PreferencesWindow";

const Home = () => {
  const constraintsRef = useRef(null);

  const [wallpaper, setWallpaper] = useState("green");
  const [preferencesOpen, togglePreferencesOpen] = useState(false);
  return (
    <motion.div
      ref={constraintsRef}
      className={
        wallpaper === "stars" ? "entire-display stars" : "entire-display"
      }
    >
      <motion.div drag dragConstraints={constraintsRef}>
        <Window />
      </motion.div>

      <motion.div drag dragConstraints={constraintsRef}>
        <Websites />
      </motion.div>
      <motion.div drag dragConstraints={constraintsRef}>
        <PreferencesIcon setWallpaper={setWallpaper} togglePreferencesOpen={togglePreferencesOpen} preferencesOpen={pre} />
      </motion.div>
      {/* <motion.div drag>
        <PreferencesWindow />
      </motion.div> */}
    </motion.div>
  );
};

export default Home;
