import { motion } from "framer-motion"

import Wallpaper from "../Wallpaper";

const PreferencesWindow = () => {
    function handleClick() {
        togglePreferencesOpen(!preferencesOpen);
        console.log(`the button was clicked`);
      }
  return (
    <div className="preferences-margin-container">
      <motion.div
        className="preferences-container"
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ duration: 0.25 }}
        className="websites-window"
      >
        <header>Control Panel</header>
        <p className="close-button" onClick={handleClick}>
          &#10006;
        </p>
        <Wallpaper setWallpaper={setWallpaper} />
      </motion.div>
    </div>
  );
};

export default PreferencesWindow;
