import {useEffect, useState} from 'react';

export const useMediaQuery = ({query}) => {


  useEffect(() => {
    const handler = e => setMatches(e.matches);
    mediaMatch.addListener(handler);
    return () => mediaMatch.removeListener(handler);
  });
  return matches;
};
