import "./styles.scss";

const ReusableWindow = ({ handleClick, title }) => {
  return (
    <div className="margin-container">
      <div className="window">
        <header>
          <p className="close-button" onClick={handleClick}>
            &#10006;
          </p>
          <p>{title}</p>
        </header>
          <div className="body-content">
              body content
          </div>
      </div>
    </div>
  );
};

export default ReusableWindow;
