import { Fragment, useRef, useState } from "react";
import { motion } from "framer-motion";
import { Resizable, ResizableBox } from "react-resizable";

import "./styles.scss";

import windowData from "../../data"

const ReusableWindow = ({
  closeWindow,
  title,
  bodyContent,
  // windowOpen,
  background,
  header,
  zIndex
}) => {
  const windowRef = useRef(null);
  const [windowOpen, setWindowOpen] = useState(`${windowData[0].title}open`);



  return (
    <div className="margin-container" ref={windowRef} style={{zIndex: `${zIndex}`}}>
      {windowOpen 
      === `${title}open` ? (
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.25 }}
          className={background ? `window ${background}-background` : `window`}
        >
          {header ? (
            <Fragment>
              <header>
                <p className="close-button" onClick={closeWindow}>
                  &#10006;
                </p>
                <p>{title}</p>
              </header>
              <div className="body-content">{bodyContent.component}</div>
            </Fragment>
          ) : (
            <div className="body-content">{bodyContent.component}</div>
          )}
        </motion.div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default ReusableWindow;
