import "./styles.scss";

const ReusableWindow = ({ handleClick, title, bodyContent, windowOpen, setWindowOpen }) => {

  return (
    <div className="margin-container">
      {windowOpen === `${title}open` ? (
        <div className="window">
          <header>
            <p className="close-button" onClick={setWindowOpen("WebsitesOpen")}>
              &#10006;
            </p>
            <p>{title}</p>
          </header>
          <div className="body-content">{bodyContent}</div>
        </div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default ReusableWindow;
