import { useState, useEffect, Fragment } from "react";
import PropTypes from "prop-types";
import plyr from "plyr";

// styles
import "./styles.module.scss";

// data
import { movies } from "../../data";

const Movies = () => {
  const [videoId, setVideoId] = useState(movies[0].url);
  const [modalOpen, setModalOpen] = useState(false);

  const openVideoModal = () => {
      setVideoId(item.url)
      setModalOpen(true);
  }

  return (
    <Fragment>
        {modalOpen ?
        <div className="videoplayer-container modal">
        <div class="modal-header">
          <span class="close-btn">&times;</span>
        </div>
        <div class="plyr__video-embed modal-content" id="player">
          <iframe
            src={videoId}
            allowfullscreen
            allowtransparency
            allow="autoplay"
          ></iframe>
        </div>
      </div>
      :
      <div></div> }

      <div className="single-item">
        <ul className="singleItem-list">
          {movies.map((item, i) => {
            return (
              <li 
              onClick={openVideoModal} 
              key={i} >
                <a href={item.url} target="_blank" className="singleItem-link">
                  {item.name}
                </a>
              </li>
            );
          })}
        </ul>
      </div>
    </Fragment>
  );
};

export default Movies;
