import { useState, useRef, Fragment, useEffect, createContext } from "react";
import { motion } from "framer-motion";

// styles
import "../home/styles.scss";

//componenets
import Icons from "../../components/Icons";
import PreferencesWindow from "../../components/PreferencesWindow";
import WebsitesWindow from "../../components/websitesWindow";
import Nav from "../../components/nav";
import ReusableWindow from "../../components/reusableWindow";
import SingleSite from "../../components/singleSite";

// data
import windowData from "../../data.js";

export const WindowCTX = createContext({});

const Home = () => {
  const constraintsRef = useRef(null);
  const iconConstraints = useRef(null);

  const [canDrag, setCanDrag] = useState(false);

  useEffect(() => {
    const mediaMatch = window.matchMedia("(min-width: 500px)");
    setCanDrag(mediaMatch.matches);
  }, []);

  const [wallpaper, setWallpaper] = useState("green");

  // create context
  // create default state
  // wrap in a provider
  // useContext Hook

  const changeWallpaper = newWallpaper => {
    setWallpaper(newWallpaper);
  };

  const [windowOpen, setWindowOpen] = useState(`${windowData[0].title}open`);

  const closeWindow = () => {
    setWindowOpen(null);
  };

  return (
    <Fragment>
      <WindowCTX.Provider>
        <motion.div
          ref={constraintsRef}
          className={
            wallpaper === "stars"
              ? "entire-display stars"
              : wallpaper === "blue"
              ? "entire-display blue"
              : "entire-display"
          }
        >
          <Nav />

          {/* {canDrag ? (
          <motion.div
          drag
          dragMomentum={false}
          dragConstraints={{
            left: -300,
            top: -50,
            bottom: 500,
            right: 300
          }}
          >
          <Window />
          </motion.div>
          ) : (
            <Window />
          )} */}

          {windowData.map(window => {
            return (
              <motion.div
                drag
                dragMomentum={false}
                dragConstraints={{
                  left: -300,
                  top: -50,
                  bottom: 500,
                  right: 300
                }}
              >
                <ReusableWindow
                  title={window.title}
                  bodyContent={window.bodyContent}
                  windowOpen={windowOpen}
                  closeWindow={closeWindow}
                  changeWallpaper={changeWallpaper}
                />
              </motion.div>
            );
          })}

          <motion.div className="icons-container" ref={iconConstraints}>
            <Icons setWindowOpen={setWindowOpen} windowOpen={windowOpen} />
          </motion.div>
        </motion.div>
      </WindowCTX.Provider>
    </Fragment>
  );
};

export default Home;
