import { motion } from "framer-motion";

import "./styles.scss";

const ReusableWindow = ({ closeWindow, title, bodyContent, windowOpen }) => {
  const blackBackground = true;
  return (
    <motion.div className={blackBackground ? `margin-container black-background` : "margin-container"}>
      {windowOpen === `${title}open` ? (
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.25 }}
          className="window"
        >
          <header>
            <p className="close-button" onClick={closeWindow}>
              &#10006;
            </p>
            <p>{title}</p>
          </header>
          <div className="body-content">{bodyContent.component}</div>
        </motion.div>
      ) : (
        <div></div>
      )}
    </motion.div>
  );
};

export default ReusableWindow;
