      <div className="single-item">
        <ul className="singleItem-list">
          {movies.map((item, i) => {
            return (
              <li className="singleItem-link" onClick={toggleModalOpen} key={i}>
                <p>{item.name}</p>
                {/* <a href={item.url} target="_blank" className="singleItem-link">
                  {item.name}
                </a> */}
              </li>
            );
          })}
        </ul>
      </div>
    </Fragment>
  );
};

export default Movies;
