import { Fragment } from "react";
import { motion } from "framer-motion";

// components
import SingleSite from "../singleSite";
const WebsitesWindow = ({ websitesOpen }) => {
  const handleClick = () => {
    websitesOpen = !websitesOpen;
  };
  return (
    <Fragment>
      {websitesOpen ? (
        <div className="margin-container">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.25 }}
            className="websites-window"
          >
            <p className="close-button" onClick={handleClick}>
              &#10006;
            </p>
            <div className="singleSite-container">
              <ul>
                <li></li>
              </ul>

              <SingleSite />
              <SingleSite />
              <SingleSite />
              <SingleSite />
            </div>
          </motion.div>
        </div>
      ) : (
        <div></div>
      )}
    </Fragment>
  );
};
export default WebsitesWindow;
